import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [patients, setPatients] = useState([]);
  const [form, setForm] = useState({
    name: '',
    age: '',
    doctor: '',
    notes: ''
  });

  const fetchPatients = async () => {
    const res = await axios.get('http://localhost:3000/api/patients');
    setPatients(res.data);
  };

  useEffect(() => {
    fetchPatients();
  }, []);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleAddPatient = async () => {
    if (!form.name || !form.age || !form.doctor) return alert('Fill all fields');
    await axios.post('http://localhost:3000/api/patients', form);
    setForm({ name: '', age: '', doctor: '', notes: '' });
    fetchPatients();
  };

  const handleAddVisit = async (id) => {
    const notes = prompt('Enter visit notes:');
    await axios.put(`http://localhost:5000/api/patients/${id}/visit`, { notes });
    fetchPatients();
  };

  return (
    <div className="container">
      <h1>üè• Patient Requisition App</h1>

      <div className="form">
        <input name="name" placeholder="Patient Name" value={form.name} onChange={handleChange} />
        <input name="age" placeholder="Age" value={form.age} onChange={handleChange} />
        <input name="doctor" placeholder="Assigned Doctor" value={form.doctor} onChange={handleChange} />
        <input name="notes" placeholder="Initial Notes" value={form.notes} onChange={handleChange} />
        <button onClick={handleAddPatient}>‚ûï Add Patient</button>
      </div>

      <div className="patient-list">
        {patients.map((patient) => (
          <div key={patient._id} className="card">
            <h3>{patient.name} (Visits: {patient.visitCount})</h3>
            <p>Age: {patient.age}</p>
            <p>Doctor: {patient.doctor}</p>
            <button onClick={() => handleAddVisit(patient._id)}>Add Visit</button>
            <h4>Visit History:</h4>
            <ul>
              {patient.visits.map((visit, i) => (
                <li key={i}>{visit.date} - {visit.notes}</li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
