import React, { useState } from 'react';
import { Box, Paper, Typography, TextField, Button, InputAdornment, IconButton, Avatar, CircularProgress, Alert } from '@mui/material';
import { Lock, Email, Visibility, VisibilityOff, LocalHospital } from '@mui/icons-material';
import apiService from '../services/api';

// Unsplash doctor image (visible and royalty-free)
const backgroundUrl = 'https://images.unsplash.com/photo-1550831107-1553da8c8464?auto=format&fit=crop&w=1500&q=80';

const DoctorLogin = () => {
  const [showPassword, setShowPassword] = useState(false);
  const [form, setForm] = useState({ email: '', password: '' });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });
  const handleTogglePassword = () => setShowPassword((show) => !show);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    try {
      // Real backend login call
      const response = await apiService.request('/doctors/login', {
        method: 'POST',
        body: JSON.stringify(form),
      });
      // You can store token or user info here
      // localStorage.setItem('doctorToken', response.token);
      window.location.href = '/dashboard'; // Redirect after login
    } catch (err) {
      setError('Invalid email/license or password.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Box
      sx={{
        minHeight: '100vh',
        width: '100vw',
        background: `url(${backgroundUrl}) center/cover no-repeat`,
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        position: 'relative',
      }}
    >
      {/* Overlay for readability */}
      <Box sx={{
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        bgcolor: 'rgba(0,0,0,0.6)',
        zIndex: 0,
      }} />
      <Paper
        elevation={8}
        sx={{
          p: 5,
          borderRadius: 4,
          minWidth: 350,
          maxWidth: 400,
          zIndex: 1,
          backdropFilter: 'blur(12px)',
          background: 'rgba(255,255,255,0.20)',
          boxShadow: '0 8px 32px 0 rgba(31, 38, 135, 0.37)',
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
        }}
      >
        <Avatar sx={{ width: 72, height: 72, mb: 2, bgcolor: 'primary.main', boxShadow: 3 }}>
          <LocalHospital sx={{ fontSize: 40 }} />
        </Avatar>
        <Typography variant="h5" fontWeight={700} mb={1} color="primary.main">
          Doctor Login
        </Typography>
        <Typography variant="body2" color="text.secondary" mb={3}>
          Welcome back! Please sign in to your account.
        </Typography>
        {error && <Alert severity="error" sx={{ width: '100%', mb: 2 }}>{error}</Alert>}
        <form style={{ width: '100%' }} onSubmit={handleSubmit}>
          <TextField
            fullWidth
            margin="normal"
            label="Email or License Number"
            name="email"
            value={form.email}
            onChange={handleChange}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <Email />
                </InputAdornment>
              ),
            }}
            required
            autoFocus
          />
          <TextField
            fullWidth
            margin="normal"
            label="Password"
            name="password"
            type={showPassword ? 'text' : 'password'}
            value={form.password}
            onChange={handleChange}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <Lock />
                </InputAdornment>
              ),
              endAdornment: (
                <InputAdornment position="end">
                  <IconButton onClick={handleTogglePassword} edge="end">
                    {showPassword ? <VisibilityOff /> : <Visibility />}
                  </IconButton>
                </InputAdornment>
              ),
            }}
            required
          />
          <Button
            fullWidth
            type="submit"
            variant="contained"
            size="large"
            sx={{ mt: 3, borderRadius: 2, fontWeight: 700, boxShadow: 2, background: 'linear-gradient(90deg, #1976d2 0%, #42a5f5 100%)' }}
            disabled={loading}
          >
            {loading ? <CircularProgress size={24} color="inherit" /> : 'Sign In'}
          </Button>
        </form>
        <Box mt={2}>
          <Button variant="text" color="secondary" size="small">
            Forgot password?
          </Button>
        </Box>
      </Paper>
    </Box>
  );
};

export default DoctorLogin;
