import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Box, Typography, Button, CircularProgress, Paper, Snackbar, Alert } from '@mui/material';
import apiService from '../services/api';

const PatientDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [patient, setPatient] = useState(null);
  const [loading, setLoading] = useState(true);
  const [visitLoading, setVisitLoading] = useState(false);
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' });

  const fetchPatient = () => {
    setLoading(true);
    apiService.request(`/patients/${id}`)
      .then(setPatient)
      .catch(() => setPatient(null))
      .finally(() => setLoading(false));
  };

  useEffect(() => {
    fetchPatient();
  }, [id]);

  const handleVisit = async () => {
    setVisitLoading(true);
    try {
      await apiService.createVisit({ patientId: id });
      setSnackbar({ open: true, message: 'Visit recorded and count increased!', severity: 'success' });
      fetchPatient();
    } catch (error) {
      setSnackbar({ open: true, message: 'Failed to record visit.', severity: 'error' });
    } finally {
      setVisitLoading(false);
    }
  };

  if (loading) return <Box p={3}><CircularProgress /></Box>;
  if (!patient) return <Box p={3}><Typography>Patient not found.</Typography></Box>;

  return (
    <Box p={3}>
      <Button variant="outlined" onClick={() => navigate('/patients')}>Back to Patients</Button>
      <Paper sx={{ p: 3, mt: 2 }}>
        <Box display="flex" alignItems="center" justifyContent="space-between" mb={2}>
          <Typography variant="h4">{patient.name}</Typography>
          <Button variant="contained" color="primary" onClick={handleVisit} disabled={visitLoading}>
            {visitLoading ? <CircularProgress size={24} color="inherit" /> : 'Visit'}
          </Button>
        </Box>
        <Typography>Email: {patient.email}</Typography>
        <Typography>Phone: {patient.phone}</Typography>
        <Typography>Gender: {patient.gender}</Typography>
        <Typography>Date of Birth: {patient.dateOfBirth ? new Date(patient.dateOfBirth).toLocaleDateString() : 'N/A'}</Typography>
        <Typography>Status: {patient.status}</Typography>
        <Typography>Address: {patient.address}</Typography>
        <Typography>Medical History: {patient.medicalHistory}</Typography>
        {/* Add more fields as needed */}
      </Paper>
      <Snackbar open={snackbar.open} autoHideDuration={3000} onClose={() => setSnackbar({ ...snackbar, open: false })}>
        <Alert onClose={() => setSnackbar({ ...snackbar, open: false })} severity={snackbar.severity} sx={{ width: '100%' }}>
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Box>
  );
};

export default PatientDetails;
